// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;
import "./utente.sol";


contract gruppo {
       
    struct Gruppo {
        uint256 id_gruppo;
        string nick_group;
        string descrizione;
        uint256 n_like;
        uint256 n_post;
    }

    struct Post {
        uint256 id_gruppo_post;
        uint256 id_post;
        string titolo;
        string descrizione;
        uint256 n_like;
        address account;
    }

    struct Commento {
        uint256 id_post_commento;
        string descrizione;
        address account;
    }

    uint256 private count_id_gruppo = 0;
    uint256 private count_id_post = 0;
    uint256 private count_id_commento = 0;
    mapping (uint256 => Gruppo) private gruppi;
    mapping (uint256 => Post) private posts;
    mapping (uint256 => Commento) private commenti;
    mapping (address => bool) private _is_like;
    utente private utenteProgetto; 

    mapping (address => bool) private accounts;
    address [] address_accounts;

    event AddressAdded(address indexed _address);


    constructor(address _utenteProgettoAddress) {
        utenteProgetto = utente(_utenteProgettoAddress);

        //non va bene, perchè così se si inscrive un nuovo utente non possediamo quest'ultimo.
        set_address ();

        // Creazione dei 10 gruppi preimpostati
        createGruppo("Sport", "Discussioni su sport e attivita' fisica");
        createGruppo("Programmazione", "Tematiche di coding e sviluppo software");
        createGruppo("Musica", "Condividi le tue passioni musicali");
        createGruppo("Cucina", "Ricette e trucchi culinari");
        createGruppo("Viaggi", "Destinazioni e consigli di viaggio");
        createGruppo("Cinema", "Film, serie TV e recensioni");
        createGruppo("Libri", "Discussioni su romanzi e letture interessanti");
        createGruppo("Videogiochi", "Tutto sui videogames e il gaming");
        createGruppo("Fitness", "Allenamenti, diete e consigli per la salute");
        createGruppo("Tecnologia", "Ultime novita' e trend tech");
    }

    modifier only_account_registred() {
        require(accounts[msg.sender], "account non registrato");
        _;
    }

    modifier only_group_exist(uint256 _id_gruppo) {
        require (_id_gruppo <= 9 && _id_gruppo >= 0, "Il Gruppo non esiste :(" );
        _;
    }

    modifier check_length_titolo(string memory _titolo) {
        require(bytes(_titolo).length <= 65, "Il titolo supera la lunghezza massima di 65 caratteri");
        _;
    }

    modifier check_like () {
        require (!_is_like[msg.sender], "Puoi mettere un like per post");
        _;
    }

    modifier check_id_post (uint256 _id_post) {
        require(_id_post>= 0 && _id_post <= count_id_post, "il post non esiste");
        _;
    }

    modifier update_accounts () {
        set_address ();
        _;
    }

    function createGruppo (string memory nick, string memory _descrizione) private  {
        Gruppo memory g = Gruppo({
            id_gruppo : count_id_gruppo,
            nick_group : nick,
            descrizione : _descrizione,
            n_like : 0,
            n_post : 0
        });

        gruppi[count_id_gruppo] = g;
        count_id_gruppo ++;
    }

    function createPost (uint256 _id_gruppo, string memory _titolo, string memory _descrizione  ) public only_account_registred only_group_exist(_id_gruppo) check_length_titolo(_titolo) update_accounts{
        Post memory post = Post ({
            id_gruppo_post : _id_gruppo,
            id_post : count_id_post,
            titolo : _titolo,
            descrizione : _descrizione,
            n_like : 0,
            account : msg.sender
        });

        posts[count_id_post] = post;
        count_id_post++;
    }

    function addLike (uint256 _id_post) public only_account_registred check_like check_id_post(_id_post) update_accounts{
        posts[_id_post].n_like ++;
    }

    function create_commento (uint256 _id_post, string memory _descrizione) public only_account_registred check_id_post(_id_post) update_accounts {
        Commento memory commento = Commento ({
            id_post_commento : _id_post,
            descrizione : _descrizione,
            account : msg.sender
        });

        commenti[count_id_commento] = commento;
        count_id_commento ++;
    }

    function set_address () private {
        address_accounts = utenteProgetto.get_address();
        uint256 i = 0;

        for (i = 0; i < address_accounts.length; i++) {
            accounts[address_accounts[i]] = true;
            emit AddressAdded(address_accounts[i]);
        }
    }

    //DOVREBBE FUNZIONARE DOMANI VEDI BENE
    function get_address_gruppo () public returns (address [] memory)  {
        set_address();
        return address_accounts;
    }

    //RIMUOVI POST
    //RIMUOVI LIKE
    //RIMUOVI COMMENTO

}

contracts/Lock.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.28;

// Uncomment this line to use console.log
// import "hardhat/console.sol";

contract Lock {
    uint public unlockTime;
    address payable public owner;

    event Withdrawal(uint amount, uint when);

    constructor(uint _unlockTime) payable {
        require(
            block.timestamp < _unlockTime,
            "Unlock time should be in the future"
        );

        unlockTime = _unlockTime;
        owner = payable(msg.sender);
    }

    function withdraw() public {
        // Uncomment this line, and the import of "hardhat/console.sol", to print a log in your terminal
        // console.log("Unlock time is %o and block timestamp is %o", unlockTime, block.timestamp);

        require(block.timestamp >= unlockTime, "You can't withdraw yet");
        require(msg.sender == owner, "You aren't the owner");

        emit Withdrawal(address(this).balance, block.timestamp);

        owner.transfer(address(this).balance);
    }
}

contracts/utente.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

contract utente {
    
    struct Utente{
        address id;
        string nick_name;
    }

    mapping ( address => Utente) private accounts;
    mapping( address => bool) private accounts_created;
    address [] private nick_name_used;

    modifier check_exists_nick_name(string memory _name) {
       uint256 i = 0;
       
        for (i = 0; i < nick_name_used.length ; i++) {
            require ((keccak256(bytes(accounts[nick_name_used[i]].nick_name))  != keccak256(bytes(_name)) ), "Mi dispiace, nick_name esistente :(");
        }
        _;
    }

    modifier check_exists_account() {
        require(!accounts_created[msg.sender], "L'account esiste, effettua il login :(");
        _;
    }

    function create_account (string memory _name) public check_exists_account check_exists_nick_name (_name){
        
        Utente memory u = Utente ({
            id : msg.sender,
            nick_name : _name
        });

        accounts[msg.sender] = u;
        nick_name_used.push(msg.sender);
        accounts_created[msg.sender] = true;
    }

    function get_utente() public view returns (string [] memory) {
        string [] memory nick_names = new string[] (nick_name_used.length);
        uint256 i= 0;

        for (i = 0; i < nick_name_used.length ; i++) {
            nick_names[i] = accounts[nick_name_used[i]].nick_name;
        }

        return nick_names;
    }

    function get_utente_registrato() public view returns (bool) {
        return accounts_created[msg.sender];
    }
    
    function get_address () public view returns (address [] memory) {
        return nick_name_used;
    }
}

